{
    "type": "object",
    "properties": {
        "musicTag": {
            "type": "string",
            "description": "the selected music tag that goes well with the video"
        },
        "description": {
            "type": "string",
            "description": "a keyword-rich description that scores at least 90% in SEO analysis"
        },
        "hashtags": {
            "type": "string",
            "description": "the relevant hashtags based on the video content so that YouTube's algorithms promote it effectively"
        }
    },
